def fix_translations():
    """–î–æ–±–∞–≤–ª—è–µ—Ç –ø–µ—Ä–µ–≤–æ–¥—ã –¥–ª—è –≤—Å–µ—Ö —Ä—É—Å—Å–∫–∏—Ö —Ç–µ–∫—Å—Ç–æ–≤"""

    if not os.path.exists(PO_FILE):
        print(f"‚ùå –§–∞–π–ª –Ω–µ –Ω–∞–π–¥–µ–Ω: {PO_FILE}")
        return False

    with open(PO_FILE, encoding="utf-8") as f:
        content = f.read()

    original_content = content
    fixes = []

    # –î–ª—è –∫–∞–∂–¥–æ–≥–æ –ø–µ—Ä–µ–≤–æ–¥–∞ –∏—â–µ–º –ø—É—Å—Ç—ã–µ msgstr –∏ –∑–∞–ø–æ–ª–Ω—è–µ–º –∏—Ö
    for russian, georgian in TRANSLATIONS.items():
        # –ò—â–µ–º –ø–∞—Ç—Ç–µ—Ä–Ω: msgid "—Ç–µ–∫—Å—Ç" \n msgstr ""
        escaped_russian = re.escape(russian)

        # –ü–∞—Ç—Ç–µ—Ä–Ω 1: –ü—Ä–æ—Å—Ç–∞—è —Å—Ç—Ä–æ–∫–∞ –±–µ–∑ —Ä–∞–∑—Ä—ã–≤–∞
        pattern1 = rf'(msgid "{escaped_russian}"\s*\n)msgstr ""'
        if re.search(pattern1, content):
            content = re.sub(pattern1, rf'\1msgstr "{georgian}"', content)
            fixes.append(f"‚úÖ '{russian[:50]}...' ‚Üí '{georgian[:50]}...'")

        # –ü–∞—Ç—Ç–µ—Ä–Ω 2: –ú–Ω–æ–≥–æ—Å—Ç—Ä–æ—á–Ω–∞—è —Å—Ç—Ä–æ–∫–∞ (msgid "" \n "—Ç–µ–∫—Å—Ç")
        if "\n" in russian or len(russian) > 60:
            # –î–ª—è –¥–ª–∏–Ω–Ω—ã—Ö —Ç–µ–∫—Å—Ç–æ–≤ –∏—â–µ–º –¥—Ä—É–≥–æ–π –ø–∞—Ç—Ç–µ—Ä–Ω
            lines = russian.split("\n")
            if len(lines) > 1:
                pattern2 = r'msgid ""\s*\n"' + re.escape(lines[0])
                if re.search(pattern2, content):
                    # –ó–∞–º–µ–Ω—è–µ–º —Ç–æ–ª—å–∫–æ –µ—Å–ª–∏ msgstr –ø—É—Å—Ç–æ–π
                    full_pattern = pattern2 + r'[^"]*"\s*\nmsgstr ""'
                    if re.search(full_pattern, content, re.DOTALL):
                        replacement = f'msgid ""\n"{lines[0]}"\nmsgstr ""\n"{georgian}"'
                        content = re.sub(full_pattern, replacement, content, flags=re.DOTALL)
                        fixes.append(f"‚úÖ Multiline: '{russian[:40]}...'")

    # –°–æ—Ö—Ä–∞–Ω—è–µ–º –∏–∑–º–µ–Ω–µ–Ω–∏—è
    if content != original_content:
        with open(PO_FILE, "w", encoding="utf-8") as f:
            f.write(content)
        print("‚úÖ –§–∞–π–ª –æ–±–Ω–æ–≤–ª–µ–Ω:", PO_FILE)
        print(f"\nüìä –î–æ–±–∞–≤–ª–µ–Ω–æ –ø–µ—Ä–µ–≤–æ–¥–æ–≤: {len(fixes)}")
        for fix in fixes[:10]:  # –ü–æ–∫–∞–∑—ã–≤–∞–µ–º –ø–µ—Ä–≤—ã–µ 10
            print(f"  {fix}")
        if len(fixes) > 10:
            print(f"  ... –∏ –µ—â–µ {len(fixes) - 10}")
